        expect(res).to.have.status(200); // Check that the status is 200
        expect(res.text).to.equal('Hello, World!'); // Check the response text
        done(); // Indicate that the test is complete
      });
  });

  // Test for the /home route
  it('should return "Welcome to the Home Page!" on GET /home', (done) => {
    chai.request(app)
      .get('/home')
      .end((err, res) => {
        expect(res).to.have.status(200);
        expect(res.text).to.equal('Welcome to the Home Page!');
        done();
      });
  });

  // Test for the /about route
  it('should return "This is the About Page." on GET /about', (done) => {
    chai.request(app)
      .get('/about')
      .end((err, res) => {
        expect(res).to.have.status(200);
        expect(res.text).to.equal('This is the About Page.');
        done();
      });
  });

});

